import React, { useState, useEffect } from "react";
import axios from "axios";
//components
import EntertainmentCardSlider from "../components/Entertainment/Entertainmentcard.component";
import Premier from "../components/Premier/Premier.component";
import PosterSlider from "../components/PosterSlider/PosterSlider.component";
import TempPosters from "../config/TempPosters.config";



const HomePage = () => {
    const [popularMovies, setPopularMovies] = useState([]);
    useEffect(() => {
        const requestPopularMovies = async () => {
            const getPopularMovies = await axios.get("/movie/popular");
            setPopularMovies(getPopularMovies.data.results);
        };
        requestPopularMovies();
    }, []);

    console.log({ popularMovies });

    return (

        <>

            <div className="flex flex-col gap-9">

                <div className="container mx-auto px-32">
                    <img src="https://in.bmscdn.com/discovery-catalog/collections/tr:w-1440,h-120/lead-in-v3-collection-202102040828.png"></img>

                    <h1 className="text-2xl font-bold text-gray-900 my-3">
                        The best of Entertainment
                    </h1>
                    <EntertainmentCardSlider />
                </div>
                <div className="bg-navCol-200 py-16">
                    <div className="container mx-auto px-4">
                        <div className="flex">
                            <img src="https://in.bmscdn.com/discovery-catalog/collections/tr:w-1440,h-120/premiere-rupay-banner-web-collection-202104230555.png"
                                alt="Rupay"
                                className="w-full h-full"
                            />
                        </div>
                        <PosterSlider images={popularMovies} title="Premiers" subtitle="Brand New Releases Every Friday" isDark />
                    </div>
                </div>

                <div className="bg-bms-700 py-16">
                    <div className="mx-auto px-32 container">
                        <img
                            src="https://in.bmscdn.com/discovery-catalog/collections/tr:w-1440,h-120/premiere-rupay-banner-web-collection-202104230555.png"
                            alt="Premier-Rupay"
                            className="w-full h-full"
                        />
                    </div>

                    <div className="mx-auto px-32 container">
                        <Premier PremierImages={popularMovies} />
                    </div>

                    {/* <div className="mx-auto px-32 container"> */}
                    {/* <PlaysFilter /> */}
                    {/* </div> */}

                    <div className="container mx-auto px-4">
                        <PosterSlider
                            images={TempPosters}
                            title="Online Streaming Events"
                            isDark={false}
                        />
                    </div>

                    <div className="container mx-auto px-4">
                        <PosterSlider
                            images={TempPosters}
                            title="Outdoor Events"
                            isDark={false}
                        />
                    </div>



                </div>

            </div>
        </>
    );
};

export default HomePage;
