import React from "react";
import HeroMovie from "../components/MovieHero/MovieHero.component";


const launchRazorPay = () => {
    let options = {
        key: "rzp_test_RDlbyFdjBg40Du",
        amount: 10000,
        currency: "INR",
        name: "BMS Clone",
        description: "Movie Purchase",
        image: "https://i.ibb.co/zPBYW3H/imgbin-bookmyshow-office-android-ticket-png.png",
        handler: () => {
            alert("Payment Done !")
        },
        theme: { color: "#c4242d" }
    };

    let rzp = new window.Razorpay(options);
    rzp.open();
};


const Movie = () => {
    return (
        <>

            <HeroMovie />
            <div className="my-12 container px-4 lg:w-1/2 lg:ml-64">
                <div className="flex flex-col items-start gap-3">
                    <h2 className="text-gray-800 font-bold text-2xl">About the movie</h2>
                    <p>For the first time in the cinematic history of Spider-Man, our friendly neighborhood hero is unmasked and no longer able to separate his normal life from the high-stakes of being a Super Hero.

                        When he asks for help from Doctor Strange the stakes become even more dangerous, forcing him to discover what it truly means to be Spider-Man.</p>
                </div>


                <button onClick={launchRazorPay} class="mt-4 bg-red-500 hover:bg-red-700 text-white font-bold py-2 px-8 rounded">
                    Book tickets
                </button>

            </div>
        </>
    );
};
export default Movie;
