import React from "react";
import { BiChevronRight } from "react-icons/bi";
import { BsSearch } from 'react-icons/bs';
const NavSm = () => {
  return (
    <><nav className="bg-gray-800 ">
      <div className="text-white flex items-center justify-between">

        <div>
          <h3 className="text-xl font-bold">It All Starts Here!</h3>
          <span className="text-gray-400 text-xs flex items-center">
            Rajkot
            <BiChevronRight />
            <div>
              <button className="bg-pink-600 text-white rounded-lg flex justify-center p-2 ">Use App</button>

            </div>
          </span>

        </div>
        <div className="w-8 h-8">
          <BsSearch className="w-full h-full" />
        </div>
      </div>

    </nav>
    </>
  )
};
const NavMd = () => {
  return (
    <>
      <div className="w-full flex gap-3 items-center bg-white px-3 py-2 rounded-md">
        <BsSearch />
        <input
          type="search"
          className="w-full "
          placeholder="Search for Movies, Events, Plays, Sports and Activities"
        />
      </div>
    </>
  );

};
const NavLg = () => { };

const Navbar = () => {
  return (
    <>
      <nav>
        <div className="md:hidden">{/*Mobile Screen*/}
          <NavSm />
        </div>
        <div className="hidden md:flex lg:hidden">{/*Tablet Screen*/}</div>
        <div className="hidden lg:flex">{/*Large Screen*/}</div>
      </nav>
    </>
  );
};

export default Navbar;
