import React from "react";
import MovieHero from "../components/MovieHero/MovieHero.component";
import { BiCameraMovie } from "react-icons/bi";
import { BsHeartFill } from "react-icins-bs";
import { IoIosArrowForward } from "react-icons-io"
const launchRazorPay = () => {
    let options = {
        key: "rzp_test_RDlbyFdjBg40Du",
        amount: 10000,
        currency: "INR",
        name: "BMS Clone",
        description: "Movie Purchase",
        image: "https://i.ibb.co/zPBYW3H/imgbin-bookmyshow-office-android-ticket-png.png",
        handler: () => {
            alert("Payment Done !")
        },
        theme: { color: "#c4242d" }
    };

    let rzp = new window.Razorpay(options);
    rzp.open();
};

// style="background-image: linear-gradient(90deg, rgb(34, 34, 34) 24.97%, rgb(34, 34, 34) 38.3%, rgba(34, 34, 34, 0.04) 97.47%, rgb(34, 34, 34) 100%), url("https://in.bmscdn.com/iedb/movies/images/mobile/listing/xxlarge/spider-man-no-way-home-et00310790-07-12-2021-07-25-42.jpg");"

const Movie = () => {
    return (
        <>

            <MovieHero />
            <div className="my-12 container px-4 lg:w-1/2 lg:ml-64">
                <div className="flex flex-col items-start gap-3">
                    <h2 className="text-gray-800 font-bold text-2xl">About the movie</h2>
                    <p>For the first time in the cinematic history of Spider-Man, our friendly neighborhood hero is unmasked and no longer able to separate his normal life from the high-stakes of being a Super Hero.

                        When he asks for help from Doctor Strange the stakes become even more dangerous, forcing him to discover what it truly means to be Spider-Man.</p>
                </div>
                <div className="my-8">
                    <hr />
                </div>
                <div className="flex flex-col items-start gap-3">
                    <h1 className="text-gray-800 font-bold text-2xl">Applicable Offers</h1>
                    <div className="flex items-start gap-2 bg-yellow-100 border-yellow-400 border-dashed border-2 rounded-md p-3 w-96">
                        <div className="w-8 h-8">
                            <BiCameraMovie className="w-full h-full" />
                        </div>
                        <div className="flex flex-col items-start">
                            <h3 className="text-gray-900 text-lg">Filmy Pass</h3>
                            <p className="text-gray-600 text-sm">Get Rs.75* off on 3 movies you buy/rent on Stream. Buy Filmy Pass @Rs.99</p>
                        </div>
                    </div>
                </div>

                <button onClick={launchRazorPay} class="mt-4 bg-red-500 hover:bg-red-700 text-white font-bold py-2 px-8 rounded">
                    Book tickets
                </button>

            </div>
        </>
    );
};
export default Movie;

<div className="w-full h-full movie-bg relative">
    <img className="w-full h-full" src="https://in.bmscdn.com/iedb/movies/images/mobile/listing/xxlarge/spider-man-no-way-home-et00310790-07-12-2021-07-25-42.jpg" alt="Movie bg"></img>
    <div className="absolute">
        <div className="border">
            <h1 className="text-4xl">
                Spider-Man: No Way Home
            </h1>
        </div>
        <div className="my-5 border flex">
            <BsHeartFill className="my-2 text-3xl text-red-500" />
            <p className="mx-2 my-2">410.3K Ratings</p>
            <IoIosArrowForward className=" text-xl mx-2 my-3" />
        </div>

    </div>
</div>
